mapper.insert:
insert into system_role_resources (id, resources_id, role_id
)
values (#{id,jdbcType=VARCHAR}, #{resourcesId,jdbcType=VARCHAR}, #{roleId,jdbcType=VARCHAR}
)

mapper.update:
update system_role_resources
set resources_id = #{resourcesId,jdbcType=VARCHAR},
role_id = #{roleId,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, resources_id, role_id
from system_role_resources
where id = #{id,jdbcType=VARCHAR}

select id, resources_id, role_id
from system_role_resources
where role_id = #{roleId,jdbcType=VARCHAR}

mapper.delete:
delete from system_role_resources
where role_id in

    #{roleResources.roleId}

or resources_id in

    #{roleResources.resourcesId}

mapper.select:
select distinct system_resources.id, system_resources.pid, system_resources.name, system_resources.url,system_resources.type,system_resources.sort from system_role_resources LEFT JOIN system_resources on system_resources.id=system_role_resources.resources_id LEFT JOIN system_user_role on system_user_role.role_id=system_role_resources.role_id where 1=1
and system_resources.type = #{type,jdbcType=VARCHAR}
and system_user_role.user_id = #{id,jdbcType=VARCHAR} ORDER BY system_resources.sort
